INSERT INTO qmac.version_operations (reference, titre, categorie, secteur_application, denomination, conditions, date_debut, date_fin, version, ah, valide, conditions_simplifiees, contribution) VALUES ('BAR-EN-101', 'Isolation de combles ou toitures (avant 01-04-2018)', 1, '&lt;p&gt;&lt;span style=&quot;color:#008080&quot;&gt;B&amp;acirc;timent r&amp;eacute;sidentiels existants.&lt;/span&gt;&lt;/p&gt;', '&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;Mise en place d&amp;#39;une isolation thermique en comble perdu ou en rampant de toiture.&lt;/span&gt;&lt;/p&gt;', '&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;La r&amp;eacute;sistance thermique R de l&amp;#39;isolation install&amp;eacute;e est sup&amp;eacute;rieure ou &amp;eacute;gale &amp;agrave; :&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;p&gt;&lt;span style=&quot;color:#45A2A2&quot;&gt;- 7 m&amp;sup2;.K/W en comble perdu ;&lt;/span&gt;&lt;/p&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color:#45A2A2&quot;&gt;- 6 m&amp;sup2;.K/W en rampant de toiture.&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color:#45A2A2&quot;&gt;La mise en place est r&amp;eacute;alis&amp;eacute;e par un professionnel.&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color:#45A2A2&quot;&gt;Si le b&amp;eacute;n&amp;eacute;ficiaire est une personne physique, le professionnel ayant r&amp;eacute;alis&amp;eacute; l&amp;#39;op&amp;eacute;ration est titulaire d&amp;#39;un signe de&lt;br /&gt;qualit&amp;eacute; r&amp;eacute;pondant aux m&amp;ecirc;mes exigences que celles pr&amp;eacute;vues &amp;agrave; l&amp;#39;article 2 du d&amp;eacute;cret n&amp;deg; 2014-812 du 16 juillet 2014&lt;br /&gt;pris pour l&amp;#39;application du second alin&amp;eacute;a du 2 de l&amp;#39;article 200 quater du code g&amp;eacute;n&amp;eacute;ral des imp&amp;ocirc;ts et du dernier alin&amp;eacute;a&lt;br /&gt;du 2 du I de l&amp;#39;article 244 quater U du code g&amp;eacute;n&amp;eacute;ral des imp&amp;ocirc;ts et des textes pris pour son application.&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color:#45A2A2&quot;&gt;Ce signe de qualit&amp;eacute; correspond &amp;agrave; des travaux relevant du 4 du I de l&amp;#39;article 46 AX de l&amp;#39;annexe III du code g&amp;eacute;n&amp;eacute;ral des imp&amp;ocirc;ts.&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color:#45A2A2&quot;&gt;La preuve de la r&amp;eacute;alisation de l&amp;#39;op&amp;eacute;ration mentionne :&lt;br /&gt;- la mise en place d&amp;#39;une isolation ;&lt;br /&gt;- et la surface d&amp;#39;isolant install&amp;eacute; ;&lt;br /&gt;- et la r&amp;eacute;sistance thermique de l&amp;#39;isolation install&amp;eacute;e.&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color:#45A2A2&quot;&gt;A d&amp;eacute;faut, la preuve de r&amp;eacute;alisation de l&amp;#39;op&amp;eacute;ration mentionne la mise en place d&amp;#39;un mat&amp;eacute;riau avec ses marque et r&amp;eacute;f&amp;eacute;rence et la surface install&amp;eacute;e,&lt;br /&gt;et elle est compl&amp;eacute;t&amp;eacute;e par un document issu du fabricant ou d&amp;#39;un organisme &amp;eacute;tabli dans l&amp;#39;Espace &amp;eacute;conomique europ&amp;eacute;en et accr&amp;eacute;dit&amp;eacute; selon la norme&lt;br /&gt;NF EN 45011 par le Comit&amp;eacute; fran&amp;ccedil;ais d&amp;#39;accr&amp;eacute;ditation (COFRAC) ou tout autre organisme d&amp;#39;accr&amp;eacute;ditation signataire de l&amp;#39;accord europ&amp;eacute;en multilat&amp;eacute;ral&lt;br /&gt;pertinent pris dans le cadre de European co-operation for Accreditation (EA), coordination europ&amp;eacute;enne des organismes d&amp;#39;accr&amp;eacute;ditation.&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color:#45A2A2&quot;&gt;Ce document indique que le mat&amp;eacute;riau de marque et r&amp;eacute;f&amp;eacute;rence mis en place est un isolant et pr&amp;eacute;cise ses caract&amp;eacute;ristiques thermiques (r&amp;eacute;sistance thermique ;&lt;br /&gt;&amp;nbsp;ou conductivit&amp;eacute; thermique et &amp;eacute;paisseur). En cas de mention d&amp;#39;une date de validit&amp;eacute;, ce document est consid&amp;eacute;r&amp;eacute; comme valable jusqu&amp;#39;&amp;agrave; un an apr&amp;egrave;s sa date de&lt;br /&gt;fin de validit&amp;eacute;. Pour les r&amp;eacute;f&amp;eacute;rences propos&amp;eacute;es en diff&amp;eacute;rentes &amp;eacute;paisseurs, la preuve de r&amp;eacute;alisation, si elle ne mentionne pas la r&amp;eacute;sistance thermique de&lt;br /&gt;l&amp;#39;isolation install&amp;eacute;e, doit imp&amp;eacute;rativement en pr&amp;eacute;ciser l&amp;#39;&amp;eacute;paisseur. Le document justificatif sp&amp;eacute;cifique &amp;agrave; l&amp;#39;op&amp;eacute;ration est la d&amp;eacute;cision de qualification&lt;br /&gt;ou de certification du professionnel d&amp;eacute;livr&amp;eacute;e selon les m&amp;ecirc;mes exigences que celles pr&amp;eacute;vues &amp;agrave; l&amp;#39;article 2 du d&amp;eacute;cret susvis&amp;eacute;.&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;', '2014-12-22', '2016-04-30', 'A14.1', '', 1, '<p> La résistance thermique R de l''isolation installée est supérieure ou égale à :</p> <br> <p>•	7 m2.K/W en comble perdu ;</p> <p>•	6 m².K/W en rampant de toiture.</p> <br> <p>La mise en place est réalisée par un professionnel.</p> <br>   <p> Si le bénéficiaire est une personne physique, le professionnel ayant réalisé l’opération est titulaire d’un signe de qualité répondant aux mêmes exigences que celles prévues à l’article 2 du décret n° 2014-812 du 16 juillet 2014 pris pour l’application du second alinéa du 2 de l’article 200 quater du code général des impôts et du dernier alinéa du 2 du I de l’article 244 quater U du code général des impôts et des textes pris pour son application.</p> <br> <p> Ce signe de qualité correspond à des travaux relevant du 4 du I de l''article 46 AX de l''annexe III du code général des impôts.</p> <br>', 1);
INSERT INTO qmac.version_operations (reference, titre, categorie, secteur_application, denomination, conditions, date_debut, date_fin, version, ah, valide, conditions_simplifiees, contribution) VALUES ('BAR-EN-102', 'Isolation des murs', 1, '&lt;p&gt;&lt;span style=&quot;color:#008080&quot;&gt;B&amp;acirc;timent r&amp;eacute;sidentiels existants.&lt;/span&gt;&lt;/p&gt;', '&lt;p&gt;&lt;span style=&quot;color:#008080&quot;&gt;Mise en place d&amp;#39;un doublage isolant (complexe ou sur ossature) sur mur(s) en fa&amp;ccedil;ade ou en pignon.&lt;/span&gt;&lt;/p&gt;', '&lt;p&gt;&lt;span style=&quot;color:#008080&quot;&gt;La r&amp;eacute;sistance thermique R de l&amp;#39;isolation install&amp;eacute;e est sup&amp;eacute;rieure ou &amp;eacute;gale &amp;agrave; 3,7 m&amp;sup2;.K/W.&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color:#008080&quot;&gt;La mise en place est r&amp;eacute;alis&amp;eacute;e par un professionnel.&lt;br /&gt;&lt;br /&gt;Si le b&amp;eacute;n&amp;eacute;ficiaire est une personne physique, le professionnel ayant r&amp;eacute;alis&amp;eacute; l&amp;rsquo;op&amp;eacute;ration est titulaire d&amp;rsquo;un signe de qualit&amp;eacute; r&amp;eacute;pondant aux m&amp;ecirc;mes exigences que celles pr &amp;eacute;vues &amp;agrave; l&amp;rsquo;article 2 du d&amp;eacute;cret n&amp;deg; 2014-812 du 16 juillet 2014 pris pour l&amp;rsquo;application du second alin&amp;eacute;a du 2 de l&amp;rsquo;article 200 quater du code g&amp;eacute;n&amp;eacute;ral des imp&amp;ocirc;ts et du dernier alin&amp;eacute;a du 2 du I de l&amp;rsquo;article 244 quater U du code g&amp;eacute;n&amp;eacute;ral des imp&amp;ocirc;ts et des textes pris pour son application.&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br /&gt;&lt;span style=&quot;color:#008080&quot;&gt;Ce signe de qualit&amp;eacute; correspond &amp;agrave; des travaux relevant du 3 du I de l&amp;#39;article 46 AX de l&amp;#39;annexe III du code g&amp;eacute;n&amp;eacute;ral des imp&amp;ocirc;ts.&lt;/span&gt;&lt;br /&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color:#008080&quot;&gt;La preuve de la r&amp;eacute;alisation de l&amp;#39;op&amp;eacute;ration mentionne :&lt;br /&gt;- la mise en place d&amp;#39;une isolation;&lt;br /&gt;- et la surface d&amp;#39;isolant install&amp;eacute;;&lt;br /&gt;- et la r&amp;eacute;sistance thermique de l&amp;#39;isolation install&amp;eacute;e.&lt;br /&gt;A d&amp;eacute;faut, la preuve de r&amp;eacute;alisation de l&amp;rsquo;op&amp;eacute;ration mentionne la mise en place d&amp;rsquo;un mat&amp;eacute;riau avec ses marque et r&amp;eacute;f&amp;eacute;rence et la surface install&amp;eacute;e, et elle est compl&amp;eacute;t&amp;eacute;e par un document issu du fabricant ou d&amp;rsquo;un organisme &amp;eacute;tabli dans l&amp;#39;Espace &amp;eacute;conomique europ&amp;eacute;en et accr&amp;eacute;dit&amp;eacute; selon la norme NF EN 45011 par le Comit&amp;eacute; fran&amp;ccedil;ais d&amp;#39;accr&amp;eacute;ditation (COFRAC) ou tout autre organisme d&amp;#39;accr&amp;eacute;ditation signataire de l&amp;#39;accord europ&amp;eacute;en multilat&amp;eacute;ral pertinent pris dans le cadre de European co-operation for Accreditation (EA), coordination europ&amp;eacute;enne des organismes d&amp;#39;accr&amp;eacute;ditation.&lt;br /&gt;&lt;br /&gt;Ce document indique que le mat&amp;eacute;riau de marque et r&amp;eacute;f&amp;eacute;rence mis en place est un isolant et pr&amp;eacute;cise ses caract&amp;eacute;ristiques thermiques (r&amp;eacute;sistance thermique; ou conductivit&amp;eacute; thermique et &amp;eacute;paisseur). En cas de mention d&amp;#39;une date de validit&amp;eacute;, ce document est consid&amp;eacute;r&amp;eacute; comme valable jusqu&amp;#39;&amp;agrave; un an apr&amp;egrave;s sa date de fin de validit&amp;eacute;. Pour les r&amp;eacute;f&amp;eacute;rences propos&amp;eacute;es en diff&amp;eacute;rentes &amp;eacute;paisseurs, la preuve de r&amp;eacute;alisation, si elle ne mentionne pas la r&amp;eacute;sistance thermique de l&amp;#39;isolation install&amp;eacute;e, doit imp&amp;eacute;rativement en pr&amp;eacute;ciser l&amp;#39;&amp;eacute;paisseur.&lt;/span&gt;&lt;br /&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color:#008080&quot;&gt;Le document justificatif sp&amp;eacute;cifique &amp;agrave; l&amp;#39;op&amp;eacute;ration est la d&amp;eacute;cision de qualification ou de certification du professionnel d&amp;eacute;livr&amp;eacute;e selon les m&amp;ecirc;mes exigences que celles por&amp;eacute;vues &amp;agrave; l&amp;#39;article 2 du d&amp;eacute;cret susvis&amp;eacute;,&lt;/span&gt;&lt;/p&gt;', '2014-12-22', '2016-04-30', 'A14.1', '', 1, '<p> La résistance thermique R de l''isolation installée est supérieure ou égale à 3,7 m².K/W.</p> <br> <p> La mise en place est réalisée par un professionnel. </p> <br> <p> Si le bénéficiaire est une personne physique, le professionnel ayant réalisé l’opération est titulaire d’un signe de qualité répondant aux mêmes exigences que celles prévues à l’article 2 du décret n° 2014-812 du 16 juillet 2014 pris pour l’application du second alinéa du 2 de l’article 200 quater du code général des impôts et du dernier alinéa du 2 du I de l’article 244 quater U du code général des impôts et des textes pris pour son application. </p> <br> <p> Ce signe de qualité correspond à des travaux relevant du 3 du I de l''article 46 AX de l''annexe III du code général des impôts.</p>', 1);
INSERT INTO qmac.version_operations (reference, titre, categorie, secteur_application, denomination, conditions, date_debut, date_fin, version, ah, valide, conditions_simplifiees, contribution) VALUES ('BAR-EN-103', 'Isolation d''un plancher', 1, '&lt;p&gt;&lt;span style=&quot;color:#008080&quot;&gt;B&amp;acirc;timents r&amp;eacute;sidentiels existants.&lt;/span&gt;&lt;/p&gt;', '&lt;p&gt;&lt;span style=&quot;color:#008080&quot;&gt;Mise en place d&amp;#39;un doublage isolant sur/sous un plancher bas situ&amp;eacute; sur un sous-sol non chauff&amp;eacute;m sur un vide sanitaire ou sur un passage ouvert.&lt;/span&gt;&lt;/p&gt;', '&lt;p style=&quot;text-align: justify;&quot;&gt;L&lt;span style=&quot;color:#008080&quot;&gt;a r&amp;eacute;sistance thermique R de l&amp;#39;isolation install&amp;eacute;e est sup&amp;eacute;rieure ou &amp;eacute;gale &amp;agrave; 3 m&amp;sup2;.K/W.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;La mise en place est r&amp;eacute;alis&amp;eacute;e par un professionnel. Si le b&amp;eacute;n&amp;eacute;ficiaire est une personne physique, le professionnel ayant r&amp;eacute;alis&amp;eacute; l&amp;rsquo;op&amp;eacute;ration est titulaire d&amp;rsquo;un signe de qualit&amp;eacute; r&amp;eacute;pondant aux m&amp;ecirc;mes exigences que celles pr&amp;eacute;vues &amp;agrave; l&amp;rsquo;article 2 du d&amp;eacute;cret n&amp;deg; 2014-812 du 16 juillet 2014 pris pour l&amp;rsquo;application du second alin&amp;eacute;a du 2 de l&amp;rsquo;article 200 quater du code g&amp;eacute;n&amp;eacute;ral des imp&amp;ocirc;ts et du dernier alin&amp;eacute;a du 2 du I de l&amp;rsquo;article 244 quater U du code g&amp;eacute;n&amp;eacute;ral des imp&amp;ocirc;ts et des textes pris pour son application.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;Ce signe de qualit&amp;eacute; correspond &amp;agrave; des travaux relevant du 3 du I de l&amp;#39;article 46 AX de l&amp;#39;annexe III du code g&amp;eacute;n&amp;eacute;ral des imp&amp;ocirc;ts.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;La preuve de la r&amp;eacute;alisation de l&amp;rsquo;op&amp;eacute;ration mentionne :&lt;br /&gt;- la mise en place d&amp;rsquo;une isolation ;&lt;br /&gt;- et la surface d&amp;rsquo;isolant install&amp;eacute; ;&lt;br /&gt;- et la r&amp;eacute;sistance thermique de l&amp;#39;isolation install&amp;eacute;e.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;A d&amp;eacute;faut, la preuve de r&amp;eacute;alisation de l&amp;rsquo;op&amp;eacute;ration mentionne la mise en place d&amp;rsquo;un mat&amp;eacute;riau avec ses marque et r&amp;eacute;f&amp;eacute;rence et la surface install&amp;eacute;e, et elle est compl&amp;eacute;t&amp;eacute;e par un document issu du fabricant ou d&amp;rsquo;un organisme &amp;eacute;tabli dans l&amp;#39;Espace &amp;eacute;conomique europ&amp;eacute;en et accr&amp;eacute;dit&amp;eacute; selon la norme NF EN 45011 par le Comit&amp;eacute; fran&amp;ccedil;ais d&amp;#39;accr&amp;eacute;ditation (COFRAC) ou tout autre organisme d&amp;#39;accr&amp;eacute;ditation signataire de l&amp;#39;accord europ&amp;eacute;en multilat&amp;eacute;ral pertinent pris dans le cadre de European co-operation for Accreditation (EA), coordination europ&amp;eacute;enne des organismes d&amp;#39;accr&amp;eacute;ditation.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;Ce document indique que le mat&amp;eacute;riau de marque et r&amp;eacute;f&amp;eacute;rence mis en place est un isolant et pr&amp;eacute;cise ses caract&amp;eacute;ristiques thermiques (r&amp;eacute;sistance thermique ; ou conductivit&amp;eacute; thermique et &amp;eacute;paisseur). En cas de mention d&amp;rsquo;une date de validit&amp;eacute;, ce document est consid&amp;eacute;r&amp;eacute; comme valable jusqu&amp;rsquo;&amp;agrave; un an apr&amp;egrave;s sa date de fin de validit&amp;eacute;. Pour les r&amp;eacute;f&amp;eacute;rences propos&amp;eacute;es en diff&amp;eacute;rentes &amp;eacute;paisseurs, la preuve de r&amp;eacute;alisation, si elle ne mentionne pas la r&amp;eacute;sistance thermique de l&amp;rsquo;isolation install&amp;eacute;e, doit imp&amp;eacute;rativement en pr&amp;eacute;ciser l&amp;rsquo;&amp;eacute;paisseur.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;Le document justificatif sp&amp;eacute;cifique &amp;agrave; l&amp;#39;op&amp;eacute;ration est la d&amp;eacute;cision de qualification ou de certification du professionnel d&amp;eacute;livr&amp;eacute;e selon les m&amp;ecirc;mes exigences que celles pr&amp;eacute;vues &amp;agrave; l&amp;#39;article 2 du d&amp;eacute;cret susvis&amp;eacute;.&lt;/span&gt;&lt;/p&gt;', '2014-12-22', null, 'A14.1', '', 1, '<p> La résistance thermique R de l''isolation installée est supérieure ou égale à 3 m2.K/W.</p>  <br> <p> La mise en place est réalisée par un professionnel.</p>  <br> <p> Si le bénéficiaire est une personne physique, le professionnel ayant réalisé l’opération est titulaire d’un signe de qualité répondant aux mêmes exigences que celles prévues à l’article 2 du décret n° 2014-812 du 16 juillet 2014 pris pour l’application du second alinéa du 2 de l’article 200 quater du code général des impôts et du dernier alinéa du 2 du I de l’article 244 quater U du code général des impôts et des textes pris pour son application. </p>  <br> <p> Ce signe de qualité correspond à des travaux relevant du 3 du I de l''article 46 AX de l''annexe III du code général des impôts.</p>', 1);
INSERT INTO qmac.version_operations (reference, titre, categorie, secteur_application, denomination, conditions, date_debut, date_fin, version, ah, valide, conditions_simplifiees, contribution) VALUES ('BAR-EN-104', 'Mise en place d''un fenêtre, fenêtre de toiture ou porte-fenêtre complète avec virage isolant', 1, '&lt;p&gt;&lt;span style=&quot;color:#008080&quot;&gt;B&amp;acirc;timents r&amp;eacute;sidentiels existants, &amp;agrave; l&amp;#39;exclusion des parties communes non chauff&amp;eacute;es.&lt;/span&gt;&lt;/p&gt;', '&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;Mise en place d&amp;#39;une fen&amp;ecirc;tre, fen&amp;ecirc;tre de toiture ou porte-fen&amp;ecirc;tre compl&amp;egrave;te avec vitrage isolant.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;Le simple remplacement de vitrages sur une fen&amp;ecirc;tre ou porte-fen&amp;ecirc;tre existante, la fermeture d&amp;#39;une loggia par des parois vitr&amp;eacute;es, la construction d&amp;#39;une v&amp;eacute;randa &amp;agrave; parois vitr&amp;eacute;es ou la cr&amp;eacute;ation d&amp;#39;une ouverture dans une paroi opaque ne donnent pas lieu &amp;agrave; la d&amp;eacute;livrance de certificats d&amp;#39;&amp;eacute;conomies d&amp;#39;&amp;eacute;nergie.&lt;/span&gt;&lt;/p&gt;', '&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;Le coefficient de transmission surfacique Uw et le facteur solaire Sw sont:&lt;br /&gt;- pour les fen&amp;ecirc;tres de toiture : Uw &amp;lt;= 1,5 W/m&amp;sup2;.K et Sw &amp;lt;= 0,36.&lt;br /&gt;- pour les autres fen&amp;ecirc;tres ou portes-fen&amp;ecirc;tres :&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; - Uw &amp;lt;- 1,3 W/m&amp;sup2;.K et Sw &amp;gt;= 0,3 ;&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; - ou Uw &amp;lt;= 1,7 W/m&amp;sup2;.K et Sw &amp;gt;= 0,36.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;La mise en place est r&amp;eacute;alis&amp;eacute;e par un professionnel.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;Si le b&amp;eacute;n&amp;eacute;ficiaire est une personne physiquem, le professionnel qui proc&amp;egrave;de &amp;agrave; la r&amp;eacute;alisation de l&amp;#39;op&amp;eacute;ratiopn doit &amp;ecirc;tre titulaire d&amp;#39;un signe de qualit&amp;eacute; r&amp;eacute;pondant aux m&amp;ecirc;mes exigences que celles pr&amp;eacute;vues &amp;agrave; l&amp;#39;article 2 du d&amp;eacute;cret n&amp;deg; 2014-812 du 16 juillet 2014 pris pour l&amp;#39;application du second alin&amp;eacute;a du 2 de l&amp;#39;article 200 quater du code g&amp;eacute;n&amp;eacute;ral des imp&amp;ocirc;ts et du dernier alin&amp;eacute;a du 2 de I de l&amp;#39;article 244 quater U du code g&amp;eacute;n&amp;eacute;ral des imp&amp;ocirc;ts et des textes pris pour son application.&lt;/span&gt;&lt;/p&gt;', '2014-12-22', null, 'A14.1', '', 1, '<p> Le coefficient de transmission surfacique Uw et le facteur solaire Sw sont :</p> <br> <p> •	pour les fenêtres de toiture : Uw <= 1,5 W/m².K et Sw <= 0,36.</p> <br> <p> •	pour les autres fenêtres ou portes-fenêtres : Uw <= 1,3 W/m2.K et Sw >= 0,3 ou Uw <= 1,7 W/m².K et Sw >= 0,36.</p> <br> <p> La mise en place est réalisée par un professionnel.</p>  <br> <p> Si le bénéficiaire est une personne physique, le professionnel qui procède à la réalisation de l’opération doit être titulaire d’un signe de qualité répondant aux mêmes exigences que celles prévues à l’article 2 du décret n° 2014812 du 16 juillet 2014 pris pour l’application du second alinéa du 2 de l’article 200 quater du code général des impôts et du dernier alinéa du 2 du I de l’article 244 quater U du code général des impôts et des textes pris pour son application. Ce signe de qualité correspond à des travaux relevant du 2 du I de l''article 46 AX de l''annexe III du code général des impôts.</p>', 1);
INSERT INTO qmac.version_operations (reference, titre, categorie, secteur_application, denomination, conditions, date_debut, date_fin, version, ah, valide, conditions_simplifiees, contribution) VALUES ('AGRI-EQ-101', 'Module d’intégration de température installé sur un ordinateur climatique', 21, '&lt;p&gt;&lt;span style=&quot;color:#2F4F4F&quot;&gt;Agriculture : serres mara&amp;icirc;ch&amp;egrave;res et horticoles neuves ou existantes.&lt;/span&gt;&lt;/p&gt;', '&lt;p&gt;&lt;span style=&quot;color:#2F4F4F&quot;&gt;Mise en place d&amp;rsquo;un module d&amp;rsquo;int&amp;eacute;gration de temp&amp;eacute;rature pour la gestion des cultures sur un ordinateur climatique neuf ou existant.&lt;/span&gt;&lt;/p&gt;', '&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#2F4F4F&quot;&gt;La mise en place est r&amp;eacute;alis&amp;eacute;e par un professionnel.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#2F4F4F&quot;&gt;La preuve de la r&amp;eacute;alisation de l&amp;rsquo;op&amp;eacute;ration mentionne la mise en place d&amp;rsquo;un module d&amp;rsquo;int&amp;eacute;gration de temp&amp;eacute;rature.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#2F4F4F&quot;&gt;A d&amp;eacute;faut, la preuve de r&amp;eacute;alisation de l&amp;rsquo;op&amp;eacute;ration mentionne la mise en place d&amp;rsquo;un &amp;eacute;quipement avec ses marque et r&amp;eacute;f&amp;eacute;rence et elle est compl&amp;eacute;t&amp;eacute;e par un document issu du fabricant indiquant que l&amp;rsquo;&amp;eacute;quipement de marque et r&amp;eacute;f&amp;eacute;rence install&amp;eacute; est un module d&amp;rsquo;int&amp;eacute;gration de temp&amp;eacute;rature.&lt;/span&gt;&lt;/p&gt;', '2014-12-22', null, 'A14.1', '', 1, '', 0);
INSERT INTO qmac.version_operations (reference, titre, categorie, secteur_application, denomination, conditions, date_debut, date_fin, version, ah, valide, conditions_simplifiees, contribution) VALUES ('AGRI-EQ-102', 'Double écran thermique', 21, '&lt;p&gt;&lt;span style=&quot;color:#2F4F4F&quot;&gt;Agriculture : serres mara&amp;icirc;ch&amp;egrave;res et horticoles neuves ou existantes.&lt;/span&gt;&lt;/p&gt;', '&lt;p&gt;&lt;span style=&quot;color:#2F4F4F&quot;&gt;Mise en place, au-dessus des cultures d&amp;rsquo;une serre chauff&amp;eacute;e, d&amp;rsquo;un double &amp;eacute;cran thermique ou d&amp;rsquo;un second &amp;eacute;cran en compl&amp;eacute;ment d&amp;rsquo;un premier existant.&lt;/span&gt;&lt;/p&gt;', '&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#2F4F4F&quot;&gt;La mise en place est r&amp;eacute;alis&amp;eacute;e par un professionnel.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#2F4F4F&quot;&gt;L&amp;rsquo;&amp;eacute;cran thermique ou le double &amp;eacute;cran thermique est pilot&amp;eacute; automatiquement.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#2F4F4F&quot;&gt;La preuve de la r&amp;eacute;alisation de l&amp;rsquo;op&amp;eacute;ration mentionne, selon le cas, la mise en place d&amp;rsquo;un double &amp;eacute;cran thermique ou d&amp;rsquo;un &amp;eacute;cran thermique.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#2F4F4F&quot;&gt;A d&amp;eacute;faut, la preuve de r&amp;eacute;alisation de l&amp;rsquo;op&amp;eacute;ration mentionne la mise en place d&amp;rsquo;un &amp;eacute;quipement avec ses marque et r&amp;eacute;f&amp;eacute;rence et elle est compl&amp;eacute;t&amp;eacute;e par un document issu du fabricant indiquant que l&amp;rsquo;&amp;eacute;quipement de marque et r&amp;eacute;f&amp;eacute;rence install&amp;eacute; est, selon le cas, un double &amp;eacute;cran thermique ou un &amp;eacute;cran thermique.&lt;/span&gt;&lt;/p&gt;', '2014-12-22', null, 'A14.1', '', 1, '', 0);
INSERT INTO qmac.version_operations (reference, titre, categorie, secteur_application, denomination, conditions, date_debut, date_fin, version, ah, valide, conditions_simplifiees, contribution) VALUES ('RES-CH-101', 'Valorisation de chaleur de récupération en réseau (France métropolitaine)', 0, '&lt;p&gt;&lt;span style=&quot;color:#800000&quot;&gt;R&amp;eacute;seaux de chaleur neufs ou existants en France m&amp;eacute;tropolitaine.&lt;/span&gt;&lt;/p&gt;', '&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#800000&quot;&gt;Mise en place d&amp;rsquo;un syst&amp;egrave;me de production de chaleur de r&amp;eacute;cup&amp;eacute;ration (valorisation &amp;eacute;nerg&amp;eacute;tique de d&amp;eacute;chets, chaleur industrielle, chaleur eaux grises, &amp;hellip;), au sens du d&amp;eacute;cret n&amp;deg;2012-394 du 23 mars 2012, sur un r&amp;eacute;seau de chaleur.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#800000&quot;&gt;Est consid&amp;eacute;r&amp;eacute; comme un r&amp;eacute;seau de chaleur, un r&amp;eacute;seau alimentant des b&amp;acirc;timents appartenant &amp;agrave; au moins deux abonn&amp;eacute;s distincts et destin&amp;eacute; aux besoins de chauffage ou de production d&amp;rsquo;eau chaude sanitaire de ces b&amp;acirc;timents.&lt;/span&gt;&lt;/p&gt;', '&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#800000&quot;&gt;La mise en place est r&amp;eacute;alis&amp;eacute;e par un professionnel.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#800000&quot;&gt;Cette op&amp;eacute;ration s&amp;rsquo;applique aux installations non soumises &amp;agrave; la directive 2003/87/CE du 13 octobre 2003 &amp;eacute;tablissant un syst&amp;egrave;me d&amp;rsquo;&amp;eacute;change de quotas d&amp;rsquo;&amp;eacute;missions de gaz &amp;agrave; effet de serre.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#800000&quot;&gt;La preuve de la r&amp;eacute;alisation de l&amp;rsquo;op&amp;eacute;ration mentionne le type d&amp;#39;&amp;eacute;quipement de chaleur de r&amp;eacute;cup&amp;eacute;ration install&amp;eacute; (incin&amp;eacute;ration, chaleur industrielle, etc.).&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#800000&quot;&gt;Le document sp&amp;eacute;cifique &amp;agrave; l&amp;rsquo;op&amp;eacute;ration est la synth&amp;egrave;se de l&amp;#39;&amp;eacute;tude sp&amp;eacute;cifique &amp;eacute;mise par le gestionnaire de r&amp;eacute;seau justifiant la quantit&amp;eacute; de chaleur nette valoris&amp;eacute;e par l&amp;rsquo;op&amp;eacute;ration (Q en kWh/an - d&amp;eacute;duction faite des pertes li&amp;eacute;es au r&amp;eacute;seau et &amp;agrave; ses &amp;eacute;quipements) destin&amp;eacute;e &amp;agrave; alimenter les b&amp;acirc;timents raccord&amp;eacute;s. Ce document permet d&amp;rsquo;identifier le r&amp;eacute;seau de chaleur concern&amp;eacute;, d&amp;eacute;crit par la zone g&amp;eacute;ographique - quartier(s), ville(s), qu&amp;rsquo;il dessert et fournit la liste des b&amp;acirc;timents concern&amp;eacute;s, raccord&amp;eacute;s au r&amp;eacute;seau &amp;agrave; la date d&amp;rsquo;ach&amp;egrave;vement de l&amp;rsquo;op&amp;eacute;ration ou pr&amp;eacute;vus dans un d&amp;eacute;lai de 3 ans apr&amp;egrave;s cette date. Ce document indique leur date pr&amp;eacute;visionnelle de raccordement.&lt;/span&gt;&lt;/p&gt;', '2015-06-29', null, 'A16.1', '', 1, '', 0);
INSERT INTO qmac.version_operations (reference, titre, categorie, secteur_application, denomination, conditions, date_debut, date_fin, version, ah, valide, conditions_simplifiees, contribution) VALUES ('RES-CH-103', 'Réhabilitation d’un poste de livraison de chaleur d’un bâtiment tertiaire', 16, '&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000080&quot;&gt;Locaux du secteur tertiaire existants de surface totale chauff&amp;eacute;e inf&amp;eacute;rieure ou &amp;eacute;gale &amp;agrave; 10 000 m2 raccord&amp;eacute;s &amp;agrave; un r&amp;eacute;seau de chauffage urbain.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000080&quot;&gt;Cette op&amp;eacute;ration ne s&amp;rsquo;applique pas aux r&amp;eacute;habilitations de postes de livraison d&amp;egrave;s lors qu&amp;rsquo;elles r&amp;eacute;duisent les &amp;eacute;missions de gaz &amp;agrave; effet de serre d&amp;rsquo;une installation class&amp;eacute;e vis&amp;eacute;e &amp;agrave; l&amp;rsquo;article L 229-5 du code de l&amp;rsquo;environnement exploit&amp;eacute;e par le b&amp;eacute;n&amp;eacute;ficiaire.&lt;/span&gt;&lt;/p&gt;', '&lt;p&gt;&lt;span style=&quot;color:#000080&quot;&gt;R&amp;eacute;habilitation d&amp;rsquo;un poste de livraison de chaleur par le remplacement de la totalit&amp;eacute; des &amp;eacute;l&amp;eacute;ments constitutifs du primaire du poste de livraison.&lt;/span&gt;&lt;/p&gt;', '&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000080&quot;&gt;Le remplacement des &amp;eacute;l&amp;eacute;ments constitutifs du primaire du poste de livraison porte sur :&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000080&quot;&gt;- l&amp;rsquo;&amp;eacute;changeur ;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000080&quot;&gt;- la r&amp;eacute;gulation primaire ;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000080&quot;&gt;- les pompes sur fluide primaire (lorsque le poste de livraison en est &amp;eacute;quip&amp;eacute;) ;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000080&quot;&gt;- l&amp;rsquo;isolation thermique.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000080&quot;&gt;La mise en place est r&amp;eacute;alis&amp;eacute;e par un professionnel.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000080&quot;&gt;La preuve de la r&amp;eacute;alisation de l&amp;rsquo;op&amp;eacute;ration mentionne le remplacement des &amp;eacute;quipements constitutifs du primaire du poste de livraison : &amp;eacute;changeurs, &amp;eacute;l&amp;eacute;ments de r&amp;eacute;gulation, &amp;eacute;l&amp;eacute;ments d&amp;#39;isolation thermique et pompes (lorsque le poste de livraison en est &amp;eacute;quip&amp;eacute;).&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#000080&quot;&gt;A d&amp;eacute;faut, la preuve de r&amp;eacute;alisation de l&amp;rsquo;op&amp;eacute;ration mentionne le remplacement des &amp;eacute;quipements constitutifs du primaire du poste de livraison avec leurs marque et r&amp;eacute;f&amp;eacute;rence et est accompagn&amp;eacute;e d&amp;rsquo;un ou plusieurs documents issu(s) du fabricant indiquant que les &amp;eacute;quipements install&amp;eacute;s sont des &amp;eacute;changeurs, des &amp;eacute;l&amp;eacute;ments de r&amp;eacute;gulation, des &amp;eacute;l&amp;eacute;ments d&amp;rsquo;isolation thermique et des pompes (lorsque le poste de livraison en est &amp;eacute;quip&amp;eacute;).&lt;/span&gt;&lt;/p&gt;', '2015-06-29', null, 'A16.1', '', 1, '', 0);
INSERT INTO qmac.version_operations (reference, titre, categorie, secteur_application, denomination, conditions, date_debut, date_fin, version, ah, valide, conditions_simplifiees, contribution) VALUES ('RES-CH-104', 'Réhabilitation d’un poste de livraison de chaleur d’un bâtiment résidentiel', 16, '&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#800000&quot;&gt;Appartements existants raccord&amp;eacute;s &amp;agrave; un r&amp;eacute;seau de chauffage urbain.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#800000&quot;&gt;Cette op&amp;eacute;ration ne s&amp;rsquo;applique pas aux r&amp;eacute;habilitations de postes de livraison d&amp;egrave;s lors qu&amp;rsquo;elles r&amp;eacute;duisent les &amp;eacute;missions de gaz &amp;agrave; effet de serre d&amp;rsquo;une installation class&amp;eacute;e vis&amp;eacute;e &amp;agrave; l&amp;rsquo;article L 229-5 du code de l&amp;rsquo;environnement exploit&amp;eacute;e par le b&amp;eacute;n&amp;eacute;ficiaire.&lt;/span&gt;&lt;/p&gt;', '&lt;p&gt;&lt;span style=&quot;color:#800000&quot;&gt;R&amp;eacute;habilitation d&amp;rsquo;un poste de livraison de chaleur par le remplacement de la totalit&amp;eacute; des &amp;eacute;l&amp;eacute;ments constitutifs du primaire du poste de livraison.&lt;/span&gt;&lt;/p&gt;', '&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#800000&quot;&gt;Le remplacement des &amp;eacute;l&amp;eacute;ments constitutifs du primaire du poste de livraison porte sur :&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#800000&quot;&gt;- l&amp;rsquo;&amp;eacute;changeur ;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#800000&quot;&gt;- la r&amp;eacute;gulation primaire ;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#800000&quot;&gt;- les pompes sur fluide primaire (lorsque le poste de livraison en est &amp;eacute;quip&amp;eacute;) ;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#800000&quot;&gt;- l&amp;rsquo;isolation thermique.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#800000&quot;&gt;La mise en place est r&amp;eacute;alis&amp;eacute;e par un professionnel.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#800000&quot;&gt;La preuve de la r&amp;eacute;alisation de l&amp;rsquo;op&amp;eacute;ration mentionne le remplacement des &amp;eacute;quipements constitutifs du primaire du poste de livraison : &amp;eacute;changeurs, &amp;eacute;l&amp;eacute;ments de r&amp;eacute;gulation, &amp;eacute;l&amp;eacute;ments d&amp;#39;isolation thermique et pompes (lorsque le poste de livraison en est &amp;eacute;quip&amp;eacute;).&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#800000&quot;&gt;A d&amp;eacute;faut, la preuve de r&amp;eacute;alisation de l&amp;rsquo;op&amp;eacute;ration mentionne le remplacement des &amp;eacute;quipements constitutifs du primaire du poste de livraison avec leurs marque et r&amp;eacute;f&amp;eacute;rence et est accompagn&amp;eacute;e d&amp;rsquo;un ou plusieurs documents issu(s) du fabricant indiquant que les &amp;eacute;quipements install&amp;eacute;s sont des &amp;eacute;changeurs, des &amp;eacute;l&amp;eacute;ments de r&amp;eacute;gulation, des &amp;eacute;l&amp;eacute;ments d&amp;rsquo;isolation thermique et des pompes (lorsque le poste de livraison en est &amp;eacute;quip&amp;eacute;).&lt;/span&gt;&lt;/p&gt;', '2015-06-29', null, 'A16.1', '', 1, '', 0);
INSERT INTO qmac.version_operations (reference, titre, categorie, secteur_application, denomination, conditions, date_debut, date_fin, version, ah, valide, conditions_simplifiees, contribution) VALUES ('RES-EC-104', 'Rénovation d’éclairage extérieur', 17, '&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#800000&quot;&gt;&amp;Eacute;clairage public ext&amp;eacute;rieur existant, autoroutier, routier, urbain, dit &amp;laquo; fonctionnel &amp;raquo;, permettant tous les types de circulation (motoris&amp;eacute;e, cycliste).&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#800000&quot;&gt;&amp;Eacute;clairage existant d&amp;rsquo;ambiances urbaines : rues, avenues, parcs, all&amp;eacute;es, voies pi&amp;eacute;tonnes.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#800000&quot;&gt;&amp;Eacute;clairage ext&amp;eacute;rieur priv&amp;eacute; existant : voiries, parkings, parcs, etc.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#800000&quot;&gt;Cette op&amp;eacute;ration ne concerne ni les illuminations de mise en valeur des sites ni l&amp;rsquo;&amp;eacute;clairage des terrains de sport.&lt;/span&gt;&lt;/p&gt;', '&lt;p&gt;&lt;span style=&quot;color:#800000&quot;&gt;R&amp;eacute;novation d&amp;rsquo;&amp;eacute;clairage ext&amp;eacute;rieur par d&amp;eacute;pose de luminaires et mise en place de luminaires neufs dont la source lumineuse peut &amp;ecirc;tre remplac&amp;eacute;e.&lt;/span&gt;&lt;/p&gt;', '&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#800000&quot;&gt;Est &amp;eacute;ligible &amp;agrave; cette action toute r&amp;eacute;novation pour laquelle chaque luminaire neuf respecte les exigences suivantes :&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#800000&quot;&gt;- ensemble optique ferm&amp;eacute; d&amp;rsquo;un degr&amp;eacute; de protection (IP) de 65 minimum ;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#800000&quot;&gt;- cas n&amp;deg;1 : efficacit&amp;eacute; lumineuse &amp;ge; 90 lumens par Watt et ULOR &amp;le; 1 % (ou, pour les luminaires &amp;agrave; LED, ULR &amp;le; 3%).&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#800000&quot;&gt;- cas n&amp;deg;2 : efficacit&amp;eacute; lumineuse &amp;ge; 70 lumens par Watt et ULOR &amp;le; 10 % (ou, pour les luminaires &amp;agrave; LED, ULR &amp;le; 15 %)&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#800000&quot;&gt;L&amp;#39;efficacit&amp;eacute; lumineuse est le ratio entre le flux lumineux initial total sortant et la puissance totale du syst&amp;egrave;me (y compris les auxiliaires). Les luminaires utilis&amp;eacute;s pour l&amp;rsquo;&amp;eacute;clairage fonctionnel des voies de circulation doivent respecter les conditions du cas n&amp;deg;1.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#800000&quot;&gt;La mise en place est r&amp;eacute;alis&amp;eacute;e par un professionnel.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#800000&quot;&gt;La preuve de r&amp;eacute;alisation de l&amp;#39;op&amp;eacute;ration mentionne : 1- la d&amp;eacute;pose des luminaires existants ; 2- la mise en place de luminaires neufs ; 3- et le nombre et les caract&amp;eacute;ristiques des luminaires neufs install&amp;eacute;s : degr&amp;eacute; de protection de l&amp;#39;ensemble optique ferm&amp;eacute; (IP), efficacit&amp;eacute; lumineuse en lumen par Watt, et ULOR (ou ULR pour les luminaires &amp;agrave; LED).&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#800000&quot;&gt;Par d&amp;eacute;rogation aux points 2 et 3 ci-dessus, la preuve de r&amp;eacute;alisation de l&amp;#39;op&amp;eacute;ration mentionne la mise en place d&amp;#39;un nombre donn&amp;eacute; d&amp;#39;&amp;eacute;quipements, identifi&amp;eacute;s par leurs marque et r&amp;eacute;f&amp;eacute;rence pr&amp;eacute;cises, et est accompagn&amp;eacute;e par un document issu du fabricant. Ce document mentionne que l&amp;#39;&amp;eacute;quipement de marque et r&amp;eacute;f&amp;eacute;rence mis en place est un luminaire, avec ses caract&amp;eacute;ristiques : degr&amp;eacute; de protection de l&amp;#39;ensemble optique ferm&amp;eacute; (IP), efficacit&amp;eacute; lumineuse en lumen par Watt, et ULOR (ou ULR pour les luminaires &amp;agrave; LED).&lt;/span&gt;&lt;/p&gt;', '2014-12-22', null, 'A14.1', '', 1, '', 0);
INSERT INTO qmac.version_operations (reference, titre, categorie, secteur_application, denomination, conditions, date_debut, date_fin, version, ah, valide, conditions_simplifiees, contribution) VALUES ('BAR-EN-105', 'Isolation des toitures terrasses', 1, '&lt;p&gt;&lt;span style=&quot;color:#008080&quot;&gt;B&amp;acirc;timents r&amp;eacute;sidentiels existants.&lt;/span&gt;&lt;/p&gt;', '&lt;p&gt;&lt;span style=&quot;color:#008080&quot;&gt;Mise en place en toiture terrasse d&amp;rsquo;un doublage ext&amp;eacute;rieur isolant.&lt;/span&gt;&lt;/p&gt;', '&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;La r&amp;eacute;sistance thermique R de l&amp;#39;isolation install&amp;eacute;e est sup&amp;eacute;rieure ou &amp;eacute;gale &amp;agrave; 4,5 m&amp;sup2;.K/W.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;La mise en place est r&amp;eacute;alis&amp;eacute;e par un professionnel.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;Si le b&amp;eacute;n&amp;eacute;ficiaire est une personne physique, le professionnel ayant r&amp;eacute;alis&amp;eacute; l&amp;rsquo;op&amp;eacute;ration est titulaire d&amp;rsquo;un signe de qualit&amp;eacute; r&amp;eacute;pondant aux m&amp;ecirc;mes exigences que celles pr&amp;eacute;vues &amp;agrave; l&amp;rsquo;article 2 du d&amp;eacute;cret n&amp;deg; 2014-812 du 16 juillet 2014 pris pour l&amp;rsquo;application du second alin&amp;eacute;a du 2 de l&amp;rsquo;article 200 quater du code g&amp;eacute;n&amp;eacute;ral des imp&amp;ocirc;ts et du dernier alin&amp;eacute;a du 2 du I de l&amp;rsquo;article 244 quater U du code g&amp;eacute;n&amp;eacute;ral des imp&amp;ocirc;ts et des textes pris pour son application.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;Ce signe de qualit&amp;eacute; correspond &amp;agrave; des travaux relevant du 4 du I de l&amp;#39;article 46 AX de l&amp;#39;annexe III du code g&amp;eacute;n&amp;eacute;ral des imp&amp;ocirc;ts.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;La preuve de la r&amp;eacute;alisation de l&amp;rsquo;op&amp;eacute;ration mentionne :&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;- la mise en place d&amp;rsquo;une isolation ;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;- et la surface d&amp;rsquo;isolant install&amp;eacute; ;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;- et la r&amp;eacute;sistance thermique de l&amp;#39;isolation install&amp;eacute;e.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;A d&amp;eacute;faut, la preuve de r&amp;eacute;alisation de l&amp;rsquo;op&amp;eacute;ration mentionne la mise en place d&amp;rsquo;un mat&amp;eacute;riau avec ses marque et r&amp;eacute;f&amp;eacute;rence et la surface install&amp;eacute;e, et elle est compl&amp;eacute;t&amp;eacute;e par un document issu du fabricant ou d&amp;rsquo;un organisme &amp;eacute;tabli dans l&amp;#39;Espace &amp;eacute;conomique europ&amp;eacute;en et accr&amp;eacute;dit&amp;eacute; selon la norme NF EN 45011 par le Comit&amp;eacute; fran&amp;ccedil;ais d&amp;#39;accr&amp;eacute;ditation (COFRAC) ou tout autre organisme d&amp;#39;accr&amp;eacute;ditation signataire de l&amp;#39;accord europ&amp;eacute;en multilat&amp;eacute;ral pertinent pris dans le cadre de European co-operation for Accreditation (EA), coordination europ&amp;eacute;enne des organismes d&amp;#39;accr&amp;eacute;ditation.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;Ce document indique que le mat&amp;eacute;riau de marque et r&amp;eacute;f&amp;eacute;rence mis en place est un isolant et pr&amp;eacute;cise ses caract&amp;eacute;ristiques thermiques (r&amp;eacute;sistance thermique ; ou conductivit&amp;eacute; thermique et &amp;eacute;paisseur). En cas de mention d&amp;rsquo;une date de validit&amp;eacute;, ce document est consid&amp;eacute;r&amp;eacute; comme valable jusqu&amp;rsquo;&amp;agrave; un an apr&amp;egrave;s sa date de fin de validit&amp;eacute;. Pour les r&amp;eacute;f&amp;eacute;rences propos&amp;eacute;es en diff&amp;eacute;rentes &amp;eacute;paisseurs, la preuve de r&amp;eacute;alisation, si elle ne mentionne pas la r&amp;eacute;sistance thermique de l&amp;rsquo;isolation install&amp;eacute;e, doit imp&amp;eacute;rativement en pr&amp;eacute;ciser l&amp;rsquo;&amp;eacute;paisseur.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;Le document justificatif sp&amp;eacute;cifique &amp;agrave; l&amp;rsquo;op&amp;eacute;ration est la d&amp;eacute;cision de qualification ou de certification du professionnel d&amp;eacute;livr&amp;eacute;e selon les m&amp;ecirc;mes exigences que celles pr&amp;eacute;vues &amp;agrave; l&amp;rsquo;article 2 du d&amp;eacute;cret susvis&amp;eacute;.&lt;/span&gt;&lt;/p&gt;', '2014-12-22', null, 'A14.1', '', 1, '<p> La résistance thermique R de l''isolation installée est supérieure ou égale à 4,5 m².K/W.</p>  <br> <p> La mise en place est réalisée par un professionnel. </p>  <br> <p> Si le bénéficiaire est une personne physique, le professionnel ayant réalisé l’opération est titulaire d’un signe de qualité répondant aux mêmes exigences que celles prévues à l’article 2 du décret n° 2014-812 du 16 juillet 2014 pris pour l’application du second alinéa du 2 de l’article 200 quater du code général des impôts et du dernier alinéa du 2 du I de l’article 244 quater U du code général des impôts et des textes pris pour son application.</p>   <br> <p> Ce signe de qualité correspond à des travaux relevant du 4 du I de l''article 46 AX de l''annexe III du code général des impôts.</p> ', 1);
INSERT INTO qmac.version_operations (reference, titre, categorie, secteur_application, denomination, conditions, date_debut, date_fin, version, ah, valide, conditions_simplifiees, contribution) VALUES ('BAR-EN-108', 'Fermeture isolante', 1, '&lt;p&gt;&lt;span style=&quot;color:#008080&quot;&gt;B&amp;acirc;timents r&amp;eacute;sidentiels existants.&lt;/span&gt;&lt;/p&gt;', '&lt;p&gt;&lt;span style=&quot;color:#008080&quot;&gt;Mise en place ou remplacement d&amp;rsquo;une fermeture isolante sur fen&amp;ecirc;tre ou porte-fen&amp;ecirc;tre existante.&lt;/span&gt;&lt;/p&gt;', '&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;La r&amp;eacute;sistance thermique additionnelle de la fermeture isolante ∆ R est telle que :&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;- ∆R &amp;gt; 0,22 m&amp;sup2;.K/W&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;La mise en place est r&amp;eacute;alis&amp;eacute;e par un professionnel.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;Si le b&amp;eacute;n&amp;eacute;ficiaire est une personne physique, le professionnel qui proc&amp;egrave;de &amp;agrave; la r&amp;eacute;alisation de l&amp;rsquo;op&amp;eacute;ration doit &amp;ecirc;tre titulaire d&amp;rsquo;un signe de qualit&amp;eacute; (r&amp;eacute;pondant aux m&amp;ecirc;mes exigences que celles pr&amp;eacute;vues &amp;agrave; l&amp;rsquo;article 2 du d&amp;eacute;cret n&amp;deg; 2014- 812 du 16 juillet 2014 pris pour l&amp;rsquo;application du second alin&amp;eacute;a du 2 de l&amp;rsquo;article 200 quater du code g&amp;eacute;n&amp;eacute;ral des imp&amp;ocirc;ts et du dernier alin&amp;eacute;a du 2 du I de l&amp;rsquo;article 244 quater U du code g&amp;eacute;n&amp;eacute;ral des imp&amp;ocirc;ts et des textes pris pour son application.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;Ce signe de qualit&amp;eacute; correspond &amp;agrave; des travaux relevant du 2 du I de l&amp;#39;article 46 AX de l&amp;#39;annexe III du code g&amp;eacute;n&amp;eacute;ral des imp&amp;ocirc;ts.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;La preuve de r&amp;eacute;alisation de l&amp;rsquo;op&amp;eacute;ration mentionne :&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;- la mise en place d&amp;rsquo;une ou plusieurs fermeture(s) ;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;- et le nombre de fermetures ;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;- et la r&amp;eacute;sistance thermique additionnelle ∆ R de la ou des fermeture(s) install&amp;eacute;e(s).&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;A d&amp;eacute;faut, la preuve de r&amp;eacute;alisation de l&amp;rsquo;op&amp;eacute;ration mentionne la mise en place d&amp;rsquo;un ou plusieurs &amp;eacute;quipements avec leur marque et r&amp;eacute;f&amp;eacute;rence et la quantit&amp;eacute; install&amp;eacute;e et elle est compl&amp;eacute;t&amp;eacute;e par un document issu du fabricant ou d&amp;rsquo;un organisme &amp;eacute;tabli dans l&amp;rsquo;Espace &amp;eacute;conomique europ&amp;eacute;en et accr&amp;eacute;dit&amp;eacute; selon la norme NF EN 45011 par le Comit&amp;eacute; fran&amp;ccedil;ais d&amp;rsquo;accr&amp;eacute;ditation (COFRAC) ou tout autre organisme d&amp;rsquo;accr&amp;eacute;ditation signataire de l&amp;rsquo;accord europ&amp;eacute;en multilat&amp;eacute;ral pertinent pris dans le cadre de European co-operation for Accreditation (EA), coordination europ&amp;eacute;enne des organismes d&amp;rsquo;accr&amp;eacute;ditation.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;Ce document indique que l&amp;rsquo;&amp;eacute;quipement de marque et r&amp;eacute;f&amp;eacute;rence mis en place est une fermeture et pr&amp;eacute;cise ses caract&amp;eacute;ristiques thermiques (r&amp;eacute;sistance thermique additionnelle). En cas de mention d&amp;rsquo;une date de validit&amp;eacute;, ce document est consid&amp;eacute;r&amp;eacute; comme valable jusqu&amp;rsquo;&amp;agrave; un an apr&amp;egrave;s sa date de fin de validit&amp;eacute;.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color:#008080&quot;&gt;Le document justificatif sp&amp;eacute;cifique &amp;agrave; l&amp;rsquo;op&amp;eacute;ration est la d&amp;eacute;cision de qualification ou de certification du professionnel d&amp;eacute;livr&amp;eacute;e selon les m&amp;ecirc;mes exigences que celles pr&amp;eacute;vues &amp;agrave; l&amp;rsquo;article 2 du d&amp;eacute;cret susvis&amp;eacute;.&lt;/span&gt;&lt;/p&gt;', '2014-12-22', null, 'A14.1', '', 1, '<p> La résistance thermique additionnelle de la fermeture isolante <u>/\\</u> R est telle que : </p><br>  <p> - <u>/\\</u>R > 0,22 m².K/W</p> <br> <p> La mise en place est réalisée par un professionnel.</p>  <br> <p> Si le bénéficiaire est une personne physique, le professionnel qui procède à la réalisation de l’opération doit être titulaire d’un signe de qualité (répondant aux mêmes exigences que celles prévues à l’article 2 du décret n° 2014- 812 du 16 juillet 2014 pris pour l’application du second alinéa du 2 de l’article 200 quater du code général des impôts et du dernier alinéa du 2 du I de l’article 244 quater U du code général des impôts et des textes pris pour son application.</p>  <br> <p>Ce signe de qualité correspond à des travaux relevant du 2 du I de l''article 46 AX de l''annexe III du code général des impôts.</p>', 1);
INSERT INTO qmac.version_operations (reference, titre, categorie, secteur_application, denomination, conditions, date_debut, date_fin, version, ah, valide, conditions_simplifiees, contribution) VALUES ('BAR-TH-104', 'Pompe à chaleur de type air/eau ou eau/eau (avant 01-02-2017)', 3, '&lt;p&gt;&lt;span style=&quot;color:#000080&quot;&gt;B&amp;acirc;timents r&amp;eacute;sidentiels existants.&lt;/span&gt;&lt;/p&gt;', '&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#000080&quot;&gt;Mise en place d&amp;rsquo;une pompe &amp;agrave; chaleur (PAC) de type air/eau ou eau/eau.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#000080&quot;&gt;Ne donnent pas lieu &amp;agrave; la d&amp;eacute;livrance de certificats d&amp;rsquo;&amp;eacute;conomies d&amp;rsquo;&amp;eacute;nergie les PAC install&amp;eacute;es en rel&amp;egrave;ve d&amp;#39;une chaudi&amp;egrave;re &amp;agrave; haute performance &amp;eacute;nerg&amp;eacute;tique et les PAC utilis&amp;eacute;es uniquement pour le chauffage de l&amp;#39;eau chaude sanitaire.&lt;/span&gt;&lt;/p&gt;', '&lt;p&gt;&lt;span style=&quot;color:#000080&quot;&gt;La mise en place est r&amp;eacute;alis&amp;eacute;e par un professionnel.&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color:#000080&quot;&gt;Si le b&amp;eacute;n&amp;eacute;ficiaire est une personne physique, le professionnel ayant r&amp;eacute;alis&amp;eacute; l&amp;rsquo;op&amp;eacute;ration est titulaire d&amp;rsquo;un signe de qualit&amp;eacute; r&amp;eacute;pondant aux m&amp;ecirc;mes exigences que celles pr&amp;eacute;vues &amp;agrave; l&amp;rsquo;article 2 du d&amp;eacute;cret n&amp;deg; 2014-812 du 16 juillet 2014 pris pour l&amp;rsquo;application du second alin&amp;eacute;a du 2 de l&amp;rsquo;article 200 quater du code g&amp;eacute;n&amp;eacute;ral des imp&amp;ocirc;ts et du dernier alin&amp;eacute;a du 2 du I de l&amp;rsquo;article 244 quater U du code g&amp;eacute;n&amp;eacute;ral des imp&amp;ocirc;ts et des textes pris pour son application.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#000080&quot;&gt;Ce signe de qualit&amp;eacute; correspond &amp;agrave; des travaux relevant du 7 du I de l&amp;#39;article 46 AX de l&amp;#39;annexe III du code g&amp;eacute;n&amp;eacute;ral des imp&amp;ocirc;ts.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:justify&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#000080&quot;&gt;&lt;strong&gt;&lt;u&gt;Pour les op&amp;eacute;rations engag&amp;eacute;es du 01/01/2015 au 25/09/2015 : &lt;/u&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#000080&quot;&gt;Le coefficient de performance (COP), est &amp;eacute;gal ou sup&amp;eacute;rieur &amp;agrave; 3,4 mesur&amp;eacute; conform&amp;eacute;ment aux conditions de performance nominales de la norme EN 14511-2 pour une temp&amp;eacute;rature &amp;agrave; la sortie de l&amp;#39;&amp;eacute;changeur thermique int&amp;eacute;rieur de 35&amp;deg;C.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:justify&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#000080&quot;&gt;&lt;strong&gt;&lt;u&gt;Pour les op&amp;eacute;rations engag&amp;eacute;es &amp;agrave; partir du 26/09/2015 : &lt;/u&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#000080&quot;&gt;L&amp;rsquo;efficacit&amp;eacute; &amp;eacute;nerg&amp;eacute;tique saisonni&amp;egrave;re (Etas), selon le r&amp;egrave;glement (EU) n&amp;deg; 813/2013 de la commission du 2 ao&amp;ucirc;t 2013, est sup&amp;eacute;rieure ou &amp;eacute;gale &amp;agrave; :&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#000080&quot;&gt;- 102% pour les PAC moyenne et haute temp&amp;eacute;rature,&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#000080&quot;&gt;- 117% pour les PAC basse temp&amp;eacute;rature.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:justify&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#000080&quot;&gt;&lt;strong&gt;&lt;u&gt;Pour les op&amp;eacute;rations engag&amp;eacute;es &amp;agrave; partir du 26/09/2017 : &lt;/u&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#000080&quot;&gt;L&amp;rsquo;efficacit&amp;eacute; &amp;eacute;nerg&amp;eacute;tique saisonni&amp;egrave;re (Etas), selon le r&amp;egrave;glement (EU) n&amp;deg; 813/2013 de la commission du 2 ao&amp;ucirc;t 2013, est sup&amp;eacute;rieure ou &amp;eacute;gale &amp;agrave;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#000080&quot;&gt;- 111% pour les PAC moyenne et haute temp&amp;eacute;rature,&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#000080&quot;&gt;- 126% pour les PAC basse temp&amp;eacute;rature.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:justify&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#000080&quot;&gt;La preuve de la r&amp;eacute;alisation de l&amp;rsquo;op&amp;eacute;ration mentionne :&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#000080&quot;&gt;- la mise en place d&amp;rsquo;une pompe &amp;agrave; chaleur air/eau ou eau/eau et, pour les op&amp;eacute;rations engag&amp;eacute;es &amp;agrave; partir du 26/09/2015, le type de pompe &amp;agrave; chaleur (basse, moyenne ou haute temp&amp;eacute;rature) ;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#000080&quot;&gt;- et la performance &amp;eacute;nerg&amp;eacute;tique de l&amp;rsquo;&amp;eacute;quipement install&amp;eacute; : selon la date d&amp;rsquo;engagement de l&amp;rsquo;op&amp;eacute;ration, le COP explicitement mesur&amp;eacute; selon la norme EN 14511-2 pour une temp&amp;eacute;rature &amp;agrave; la sortie de l&amp;#39;&amp;eacute;changeur thermique int&amp;eacute;rieur de 35&amp;deg;C, ou l&amp;rsquo;Etas.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:justify&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#000080&quot;&gt;A d&amp;eacute;faut, la preuve de r&amp;eacute;alisation de l&amp;rsquo;op&amp;eacute;ration mentionne la mise en place d&amp;rsquo;un &amp;eacute;quipement avec ses marque et r&amp;eacute;f&amp;eacute;rence et elle est compl&amp;eacute;t&amp;eacute;e par un document issu du fabricant ou d&amp;rsquo;un organisme &amp;eacute;tabli dans l&amp;#39;Espace &amp;eacute;conomique europ&amp;eacute;en et accr&amp;eacute;dit&amp;eacute; selon la norme NF EN 45011 par le Comit&amp;eacute; fran&amp;ccedil;ais d&amp;#39;accr&amp;eacute;ditation (COFRAC) ou tout autre organisme d&amp;#39;accr&amp;eacute;ditation signataire de l&amp;#39;accord europ&amp;eacute;en multilat&amp;eacute;ral pertinent pris dans le cadre de European co-operation for Accreditation (EA), coordination europ&amp;eacute;enne des organismes d&amp;#39;accr&amp;eacute;ditation.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#000080&quot;&gt;Ce document indique :&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#000080&quot;&gt;- que l&amp;rsquo;&amp;eacute;quipement de marque et r&amp;eacute;f&amp;eacute;rence mis en place est une pompe &amp;agrave; chaleur air/eau ou eau/eau et, pour les op&amp;eacute;rations engag&amp;eacute;es &amp;agrave; partir du 26/09/2015, le type de pompe &amp;agrave; chaleur (basse, moyenne ou haute temp&amp;eacute;rature) :&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#000080&quot;&gt;- et la performance &amp;eacute;nerg&amp;eacute;tique de l&amp;rsquo;&amp;eacute;quipement install&amp;eacute; : selon la date d&amp;rsquo;engagement de l&amp;rsquo;op&amp;eacute;ration, le COP explicitement mesur&amp;eacute; selon la norme EN 14511-2 pour une temp&amp;eacute;rature &amp;agrave; la sortie de l&amp;#39;&amp;eacute;changeur thermique int&amp;eacute;rieur de 35&amp;deg;C, ou l&amp;rsquo;Etas.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:justify&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#000080&quot;&gt;En cas de mention d&amp;rsquo;une date de validit&amp;eacute;, ce document est consid&amp;eacute;r&amp;eacute; comme valable jusqu&amp;rsquo;&amp;agrave; un an apr&amp;egrave;s sa date de fin de validit&amp;eacute;.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:justify&quot;&gt;&lt;span style=&quot;color:#000080&quot;&gt;Le document justificatif sp&amp;eacute;cifique &amp;agrave; l&amp;rsquo;op&amp;eacute;ration est la d&amp;eacute;cision de qualification ou de certification du professionnel d&amp;eacute;livr&amp;eacute;e selon les m&amp;ecirc;mes exigences que celles pr&amp;eacute;vues &amp;agrave; l&amp;rsquo;article 2 du d&amp;eacute;cret susvis&amp;eacute;.&lt;/span&gt;&lt;/p&gt;', '2014-12-22', '2017-01-31', 'A14.1', '', 1, '<p> La mise en place est réalisée par un professionnel. </p> <br> <p> Si le bénéficiaire est une personne physique, le professionnel ayant réalisé l’opération est titulaire d’un signe de qualité répondant aux mêmes exigences que celles prévues à l’article 2 du décret n° 2014-812 du 16 juillet 2014 pris pour l’application du second alinéa du 2 de l’article 200 quater du code général des impôts et du dernier alinéa du 2 du I de l’article 244 quater U du code général des impôts et des textes pris pour son application. </p> <br> <p> Ce signe de qualité correspond à des travaux relevant du 7 du I de l''article 46 AX de l''annexe III du code général des impôts. </p> <br> <p> Pour les opérations engagées à partir du 26/09/2017 : L’efficacité énergétique saisonnière (ETAS) selon le règlement (EU) n° 813/2013 de la commission du 2 août 2013 supérieure ou égale à : </p> <p> - 111% pour les PAC moyenne et haute température,</p> <p> - 126% pour les PAC basse température.</p>', 0);
INSERT INTO qmac.version_operations (reference, titre, categorie, secteur_application, denomination, conditions, date_debut, date_fin, version, ah, valide, conditions_simplifiees, contribution) VALUES ('BAR-TH-106', 'Chaudière individuelle à haute performance énergétique (avant 01-02-2017)', 2, '<p style="text-align: justify;">Bâtiments résidentiels existants.</p>', '<p>Mise en place d’une chaudière individuelle à haute performance énergétique.
</p>', '<p>La mise en place est réalisée par un professionnel.<br/>
