INSERT INTO qmac.version_operations (reference, titre, categorie, secteur_application, denomination, conditions, date_debut, date_fin, version, ah, valide, conditions_simplifiees, contribution) VALUES ('RES-EC-107', 'Horloge astronomique pour l’éclairage extérieur', 17, 'Éclairage public extérieur existant : autoroutier, routier, urbain, dit « fonctionnel », permettant tous les types de circulation (motorisée, cycliste).
